import { useEffect } from 'react';
import './Apps.scss';

const appsList = [
  {
    id: 1,
    name: 'Health Tracker',
    tagline: 'Your daily wellness companion.',
    description:
      'Monitor steps, vitals, water intake and heart rate. Sync with wearables and share progress.',
    icon: 'data:image/jpeg;base64,',
    screenshot: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS7MNyUqg36TCVJSkVIno5zlYA3b9izB6BBKw&s',
    playStoreUrl: '#',
  },
  {
    id: 2,
    name: 'Task Manager',
    tagline: 'Be more productive every day.',
    description:
      'Organize tasks, set reminders, track habits. Smart calendar integration keeps you on schedule.',
    icon: 'data:image/jpeg;base64,',
    screenshot: 'data:image/png;base64,',
    playStoreUrl: '#',
  },
  {
    id: 3,
    name: 'Finance Buddy',
    tagline: 'Smart budgeting in your pocket.',
    description:
      'Monitor spending, set savings goals, and review insights. Safe, encrypted, and easy to use.',
    icon: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR_E11M-_16qL8wuKcc8Mr0koL390V6LupY_A&s',
    screenshot: 'data:image/jpeg;base64,',
    playStoreUrl: '#',
  },
];

export default function Apps() {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <div className="apps-page">
      <section className="apps-hero">
        <div className="hero-inner">
          <h1>Explore Our Android Applications</h1>
          <p>
            Discover our modern, secure, and user‑centric mobile apps. Stay tuned for upcoming releases and exclusive preview access.
          </p>
        </div>
      </section>

      <section className="app-catalog container">
        <h2>Our Apps</h2>
        <div className="cards-grid">
          {appsList.map((app, idx) => (
            <div key={app.id} className="app-card">
              <div className="icon-zone">
                <img src={app.icon} alt={`${app.name} icon`} />
              </div>
              <h3>{app.name}</h3>
              <p className="tagline">{app.tagline}</p>
              <img
                src={app.screenshot}
                alt={`${app.name} screenshot`}
                className="screenshot"
              />
              <p className="description">{app.description}</p>
              <div className="actions">
                <a
                  href={app.playStoreUrl}
                  className="btn btn-primary"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  View on Play Store
                </a>
                <button className="btn btn-secondary">Request Beta</button>
              </div>
            </div>
          ))}
        </div>
      </section>

      <section className="company-strengths">
        <div className="strengths-inner">
          <div className="item">
            <h4>🔐 Privacy First</h4>
            <p>All apps follow GDPR‑compliant data protection standards.</p>
          </div>
          <div className="item">
            <h4>📦 Future‑Ready</h4>
            <p>Lightweight packages with frequent updates and improvements.</p>
          </div>
          <div className="item">
            <h4>🌟 Beta Access</h4>
            <p>Early access invites and roadmap previews for subscribers.</p>
          </div>
        </div>
      </section>

      <section className="discover-more">
        <p>Don’t see your app listed yet? We’ll have more products launching soon.</p>
        <button className="btn btn-cta">Join Early Access List</button>
      </section>
    </div>
  );
}
